import React, { useEffect } from 'react';
import planeImage from '/Users/mehreenaiman/Documents/GitHub/FlightForesight/API Model Deployment/flight-foresight/src/images/airplane-7-64.png';

const BackgroundTravelWebsite = () => {
    // Apply body styles on mount
    useEffect(() => {
        document.body.style.backgroundColor = 'rgb(241, 241, 244)'; // White
        document.body.style.fontFamily = 'Arial, sans-serif';
        document.body.style.margin = '0';
        document.body.style.padding = '0';
        document.body.style.color = '#333';
    }, []); // Empty dependency array means it runs only once when the component mounts

    return (
        <div>
            {/* Header section */}
            <div style={{
                backgroundColor: 'rgb(251, 251, 251)',  
                color: '#000000',            
                textAlign: 'left',
                paddingLeft: '20px',
                display: 'flex',
                alignItems: 'center',
                padding: '10px',             // Add padding around the header for spacing
                borderBottom: '10px solid #000080' // Thin black line under the header
            }}>
                <img 
                    src={planeImage} 
                    alt="JetSet" 
                    style={{
                        width: '40px', 
                        height: 'auto', 
                        transform: 'rotate(1800deg)',  // Rotate the image
                        marginRight: '20px'    // Adds spacing between image and text
                    }} 
                />
                <h1 style={{ fontSize: '40px', color: 'rgb(0,0,128' }}>jetSet</h1>       
            </div>

            {/* Light Grey Rectangle below the header */}
            <div style={{
                backgroundColor: 'rgb(226, 226, 228)', // Light Grey
                height: '200px',            // Height of the rectangle (increased to accommodate search boxes)
                marginTop: '70px',          // Adds some space between the header and the rectangle
                marginRight: '20px', 
                marginLeft: '20px',
                width: '93%',              // Full width of the screen
                padding: '20px',            // Adds padding inside the rectangle
                textAlign: 'center',
                borderRadius: '10px',      // Adds rounded corners
                display: 'flex',           // Flexbox to align elements
                flexDirection: 'column',   // Align items in a column
                justifyContent: 'center'   // Vertically center the content inside the rectangle
            }}>

            <div style={{ display: 'flex' }}>
                <h2 style={{
                    color: '#000000',
                    fontSize: '40px',
                    textAlign: 'left',
                    marginRight: '10px',
                    fontFamily: 'Bebas Neue' // Applying the font
                }}>
                    explore the world with
                </h2>
                <h2 style={{
                    color: 'rgb(0,0,128)',
                    fontSize: '40px',
                    textAlign: 'left',
                    fontFamily: 'Bebas Neue' // Applying the font
                }}>
                    jetSet.
                </h2>
            </div>

                {/* Search Boxes inside the grey rectangle */}
                <div style={{
                    display: 'flex',                // Flexbox to position the search boxes side by side
                    justifyContent: 'space-between', // Space them evenly
                    width: '100%',                 // Full width of the rectangle
                    marginTop: '20px',             // Space between the title and the search boxes
                }}>
                    <input 
                        type="text" 
                        placeholder="from?" 
                        style={{
                            width: '15%',         // Each input box takes 45% of the width
                            padding: '10px',      // Adds padding inside the input field
                            borderRadius: '5px',  // Adds rounded corners to the input
                            border: '3px solid #ccc', // Light border color
                            fontSize: '16px'      // Text size
                        }} 
                    />
                    <input 
                        type="text" 
                        placeholder="to?" 
                        style={{
                            width: '15%',         // Each input box takes 45% of the width
                            padding: '10px',      // Adds padding inside the input field
                            borderRadius: '5px',  // Adds rounded corners to the input
                            border: '3px solid #ccc', // Light border color
                            fontSize: '16px'      // Text size
                        }} 
                    />
                </div>
            </div>
        </div>
    );
};

export default BackgroundTravelWebsite;
